public class Day2 {

    static int posX = 1;
    static int posY = 1;

    static int x = 0;
    static int y = 2;

    static String codeNum = "";
    static String codeStar = "";

    static int[][] numpad = {
            {1, 2, 3},
            {4, 5, 6},
            {7, 8, 9}
    };
    static char[][] starpad = {
            {'0', '0', '1', '0', '0'},
            {'0', '2', '3', '4', '0'},
            {'5', '6', '7', '8', '9'},
            {'0', 'A', 'B', 'C', '0'},
            {'0', '0', 'D', '0', '0'},
    };

    public static void main(String[] args) {
        String inNum = "\n" +
                "\n" +
                "\n" +
                "\n" +
                "\n";
        String[] partsNum = inNum.split("\n");
        String inStar = "\n" +
                "\n" +
                "\n" +
                "\n" +
                "\n";
        String[] partsStar = inStar.split("\n");
        for (int i = 0; i < partsNum.length; i++) {
            moveNum(partsNum[i]);
        }
        for (int i = 0; i < partsStar.length; i++) {
            moveStar(partsStar[i]);
        }
        //System.out.println(codeNum);
        System.out.println(codeStar);
    }

    private static int mDistToCenter() {
        return Math.abs(x - 2) + Math.abs(y - 2);
    }

    private static void moveStar(String cmd) {
        char input;
        for (int i = 0; i < cmd.length(); i++) {
            input = cmd.charAt(i);
            switch (input) {
                case 'U':
                    int u = y;
                    y = Math.max(0, y - 1);
                    if (mDistToCenter() > 2) y = u;
                    break;
                case 'D':
                    int t = y;
                    y = Math.min(4, y + 1);
                    if (mDistToCenter() > 2) y = t;
                    break;
                case 'L':
                    int r = x;
                    x = Math.max(0, x - 1);
                    if (mDistToCenter() > 2) x = r;
                    break;
                case 'R':
                    int o = x;
                    x = Math.min(starpad[y].length-1, x + 1);
                    if (mDistToCenter() > 2) x = o;
                    break;
            }
        }
        System.out.println("[" + x + ", " + y + "]");
        codeStar += starpad[y][x];
    }

    private static void moveNum(String cmd) {
        char input;
        for (int i = 0; i < cmd.length(); i++) {
            input = cmd.charAt(i);
            switch (input) {
                case 'U':
                    posY = Math.max(0, posY - 1);
                    break;
                case 'D':
                    posY = Math.min(2, posY + 1);
                    break;
                case 'L':
                    posX = Math.max(0, posX - 1);
                    break;
                case 'R':
                    posX = Math.min(2, posX + 1);
                    break;
            }
        }
        codeNum += numpad[posY][posX];
    }
}
